export const MANUAL_RECONNECT_SIGNAL="SIGUSR1";export const detachManualReconnectSignal=(n,e=process)=>{if(n.handler){try{e.off("SIGUSR1",n.handler)}catch{}n.handler=null}};export const registerManualReconnectSignal=n=>{const e=n.runtime??process;detachManualReconnectSignal(n.state,e),n.state.handler=n.onReconnect;try{e.on("SIGUSR1",n.onReconnect),n.logger.info("manual reconnect enabled signal=SIGUSR1")}catch{n.logger.warn("manual reconnect signal not supported in current runtime")}};